/**
 *
 * @param data 判断真假判断所需的数据
 */
const isBlanks: <T>(data:T) => boolean = (data) => {
    if(data == Infinity) {
        return !!data;
    }
    if (typeof data === 'object' && data !== null) {
        const propsArr = Object.getOwnPropertyNames(data);
        return !propsArr.length
    }
    return !data;
}

export default isBlanks;
