import React from "react";
import { Navigator, Battery } from "../../global";
const useBattery = () => {
  React.useEffect(() => {
    const [battery, setBattery] = React.useState<Battery>();
    const nav = navigator as any as Navigator;
    nav.getBattery().then(bat => {
      setBattery(bat)
    })
  }, [])
  return battery
}

export default useBattery;
